import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Sidebar from './Sidebar'; // Import Sidebar

const InstructorManageClasses = () => {
    const [user, setUser] = useState(null);
    const [classes, setClasses] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [newClassName, setNewClassName] = useState('');
    const [message, setMessage] = useState('');

    // Fetch current classes from the backend when the component mounts
    useEffect(() => {
        const fetchClasses = async () => {
            try {
                const response = await axios.get('http://localhost:3000/instructorManageClasses', {
                    withCredentials: true,
                });
                setClasses(response.data.classes);
            } catch (error) {
                console.error('Error fetching classes:', error);
            }
        };

        fetchClasses();

        const fetchUser = async () => {
            try {
                const response = await axios.get('http://localhost:3000/index', {
                    withCredentials: true
                });
                setUser(response.data.user);
                setMessage(response.data.message);
            } catch (error) {
                setMessage(error.response?.data?.message || 'Failed to fetch user');
            } finally {
                setLoading(false); // Always stop loading regardless of success/failure
            }
        };

        fetchUser();
    }, []);

    // Handle file selection for the new class roster
    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    // Upload the CSV file to create a new class
    const handleUpload = async () => {
        if (!selectedFile || !newClassName.trim()) {
            setMessage('Please provide a class name and select a file.');
            return;
        }

        const formData = new FormData();
        formData.append('roster', selectedFile);
        formData.append('className', newClassName.trim());

        try {
            const response = await axios.post('http://localhost:3000/upload-roster', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
                withCredentials: true,
            });

            setMessage(response.data.message);
            // Refresh the classes list
            setClasses([...classes, { name: newClassName.trim(), studentCount: response.data.studentCount, groupCount: response.data.groupCount }]);
        } catch (error) {
            setMessage(error.response?.data?.message || 'Failed to upload roster');
        }
    };

    return (
        <div className="manage-classes-container" style={{ display: 'flex' }}>
            <Sidebar /> {/* Include Sidebar component */}
            <div className="content" style={{ padding: '20px', flex: 1 }}>
                <h2>Manage Classes</h2>
                {/* Display current classes */}
                <div>
                    <h3>Current Classes</h3>
                    
                </div>

                {/* File upload section */}
                <div style={{ marginTop: '20px', borderTop: '1px solid #ccc', paddingTop: '10px' }}>
                    <h3>Add a New Class</h3>
                    <input
                        type="text"
                        placeholder="Enter Class Name"
                        value={newClassName}
                        onChange={(e) => setNewClassName(e.target.value)}
                    />
                    <br />
                    <input type="file" accept=".csv" onChange={handleFileChange} />
                    <button onClick={handleUpload}>Upload Roster</button>
                    {message && <p>{message}</p>}
                </div>
            </div>
        </div>
    );
};

export default InstructorManageClasses;
